mapper.select:
select 

from sys_staff sys_staff
where sys_staff.id = #{id,jdbcType=INTEGER}

mapper.update:
update sys_staff
set status = 0
where id in #{id,jdbcType=INTEGER}

mapper.select:
select stf.id, stf.username, stf.sex, stf.user_height, stf.phone, DATE_FORMAT(stf.create_time,'%Y-%m-%d %H:%i:%s') as create_time, stf.head_img, stf.identity, stf.position,
stf1.username as accountName,
stf.remark from sys_staff stf
left join sys_staff stf1 on stf1.user_id = stf.create_account_id
stf.`status` = 1
and stf.create_account_id = #{accountId}
and stf.username like concat('%', #{userName}, '%')
and stf.create_time >= #{startTime}
and stf.create_time <= #{endTime}
order by stf.create_time desc
