mapper.select:
select 

from sys_user sys_user
where sys_user.id = #{id,jdbcType=INTEGER}

select sys_user.id as sys_user_id, sys_user.account as sys_user_account, sys_user.`password` as `sys_user_password`, sys_user.phone as sys_user_phone, sys_user.address as sys_user_address, sys_user.address_code as sys_user_address_code, staff.id as staff_id, staff.username as staff_username, staff.head_img as staff_head_img from sys_user sys_user left join sys_staff staff on sys_user.id = staff.user_id
sys_user.status = 1 and staff.status = 1
and account = #{account}
and phone = #{phone}

select su.id,su.account,su.address,stf.remark,stf.phone from sys_user su, sys_staff stf
su.status = 1 and stf.user_id = su.id
and stf.create_account_id = #{accountId}
and account like concat('%', #{accountName}, '%')
and su.address_code = #{addressCode}
order by su.id desc

mapper.insert:
insert into sys_user (account, `password`, phone, 
  address, `status`, address_code
  )
values (#{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, 
  #{address,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{address_code,jdbcType=VARCHAR}
  )
